          setPopupMessage("Email marked as read");
          handleClick();
        })
        .catch((error) => {
          console.log(error);
        });
    }
  }

  function markAsUnreadClick(email) {
    let emailInfo = allEmails.length
      ? allEmails.filter((e) => e._id === email._id)
      : "";
    if (emailInfo[0].read === false) {
      setPopupMessage("Email already marked as unread");
      handleClick();
    } else {
      let updatedEmailDetails = {
        read: false,
      };
      axios
        .put(
          `https://gmail-clone-email-be.onrender.com/updateEmailInfo/${email._id}`,
          updatedEmailDetails
        )
        .then((response) => {
          setPopupMessage("Email marked as unread");
          handleClick();
        })
        .catch((error) => {
          console.log(error);
        });
    }
  }

  function emailClick(email) {
    setOpenEmail(true);
    setTopIcons(true);
    setViewEmail(email);
  }

  function backClick(email) {
    let emailInfo = allEmails.length
      ? allEmails.filter((e) => e._id === email._id)
      : "";
    if (emailInfo[0].read !== true) {
      let updatedEmailDetails = {
        read: true,
      };
      axios
        .put(
          `https://gmail-clone-email-be.onrender.com/updateEmailInfo/${email._id}`,
          updatedEmailDetails
        )
        .then((response) => {
          // console.log(response.data);
        })
        .catch((error) => {
          console.log(error);
        });
      setOpenEmail(false);
      setTopIcons(false);
    } else {
      setOpenEmail(false);
      setTopIcons(false);
    }
  }

  function spamClick(email) {
    let spamEmail = {
      starred: false,
      important: false,
      read: "",
      spam: true,
    };
    axios
      .put(
        `https://gmail-clone-email-be.onrender.com/updateEmailInfo/${email._id}`,
        spamEmail
      )
      .then((response) => {
        setPopupMessage("Email marked as spam");
        handleClick();
        setOpenEmail(false);
        setTopIcons(false);
      })
      .catch((error) => {
        console.log(error);
      });
  }

  function markUnread(email) {
    let updatedEmailDetails = {
      read: false,
    };
    axios
      .put(
        `https://gmail-clone-email-be.onrender.com/updateEmailInfo/${email._id}`,
        updatedEmailDetails
      )
      .then((response) => {
        setPopupMessage("Email marked as unread");
        handleClick();
        setOpenEmail(false);
        setTopIcons(false);
      })
      .catch((error) => {
        console.log(error);
      });
