          updatedEmailDetails
        )
        .then((response) => {
          setPopupMessage("Email marked as important");
          handleClick();
        })
        .catch((error) => {
          console.log(error);
        });
    }
  }

  return (
    <>
      <Navbar />
      
      {open ? (
        <Snackbar
          open={open}
          autoHideDuration={3000}
          onClose={handleClose}
          message={popMessage}
          action={action}
        />
      ) : (
        ""
      )}
    </>
  );
}

